import React, { useEffect, useState } from 'react';
import { FlatList,Button, Text, View, StyleSheet,ActivityIndicator } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import Spinner from 'react-native-loading-spinner-overlay';
import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    
    StackedBarChart
  } from "react-native-chart-kit";

  import { Dimensions,TouchableHighlight, Image,TouchableOpacity, } from "react-native";

  export default function TestingSensorPage({navigation}) {

    const pressHandler =() =>{
      navigation.navigate('TestingLoadingPage');
  }

  const [isLoading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  console.log(data);

  useEffect(() => {
    fetch('https://raw.githubusercontent.com/adhithiravi/React-Hooks-Examples/master/testAPI.json')
      .then((response) => response.json())
      .then((json) => setData(json))
      .catch((error) => console.error(error))
      .finally(() => setLoading(false));
  }, []);

  const dataPie = {
    labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
    data: [,0.4, 0.7, 0.8, 0.3]
  };

//   const dataPie = {
//     labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
//     data: [,data.ph, data.temp, data.turbidity, data.conductivity]
//   };


  return (

    <View style={{ flex: 1, padding: 0 }}>
      {isLoading ? <Text>Loading...</Text> : 
      (
          
          <View style={{ flex: 1, flexDirection: 'column', justifyContent:  'space-between'}}>

        <View style = {styles.headContainer}>
            <LinearGradient
                // Button Linear Gradient
                colors={['#020058', '#030096']} 
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style={styles.headContent}
                >
                <Image source={require('../assets/back-arrow.png')} style = {styles.BackArrow} />
                <Text style={styles.HeadText}>Quality Test</Text>
                <Text style={styles.HeadTextCap}>Water quality results</Text>
            </LinearGradient>
        </View>

        <View style = {styles.InnerHeadContent}>
                
            <Text style = {styles.InnerHeadText}>Sensor testing</Text>
            <Text style={styles.InnerHeadTextCap}>make sure all the sensors are  in water</Text>
        </View>

          <View style = {styles.Instruction}>
      
            <TouchableOpacity style={styles.CheckButtonTemp} >
                <Image source={require('../assets/check-circle-white.png')} style = {styles.StartLogo} />
                <Text style={styles.CheckButtonText}>Tempurature sensor<br/><Text style={styles.CheckBtnTxtCap}>Dics about it</Text></Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.CheckButtonPh} >
                <Image source={require('../assets/check-circle-white.png')} style = {styles.StartLogo} />
                <Text style={styles.CheckButtonText}>pH sensor<br/><Text style={styles.CheckBtnTxtCap}>Dics about it</Text></Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.CheckButtonTurbidity} >
                <Image source={require('../assets/check-circle-white.png')} style = {styles.StartLogo} />
                <Text style={styles.CheckButtonText}>Turbidity sensor<br/><Text style={styles.CheckBtnTxtCap}>Dics about it</Text></Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.CheckButtonCndctvty} >
                <Image source={require('../assets/check-circle-white.png')} style = {styles.StartLogo} />
                <Text style={styles.CheckButtonText}>Conductivity sensor<br/><Text style={styles.CheckBtnTxtCap}>Dics about it</Text></Text>
            </TouchableOpacity>
            
          </View>
          <View style = {styles.BottomButtomContainer}>
            <TouchableOpacity style={styles.BottomButtom}  onPress={pressHandler} >
              <Text style={styles.BottomButtomText}>Next</Text>
            </TouchableOpacity>
            <Text style={styles.BottomButtomCapText}>How to use this device? <Text style={styles.BottomButtomCapTextLink}>More info</Text></Text>
          </View>

            
        </View>
      )}
    </View>
  );
};

  const AppButton = ({ onPress, title }) => (
    <TouchableOpacity onPress={onPress} style={styles.appButtonContainer}>
      <Text style={styles.appButtonText}>{title}</Text>
    </TouchableOpacity>
  );

  const MyActivityIndicator = () => {
	return (
      	<View style={{ flex: 1, justifyContent: "center"}}>
      		//size can be "small" or "large"
			<ActivityIndicator size="large" color="#00ff00" />
      	</View>
    );
}

// export default MyActivityIndicator;

  const styles = StyleSheet.create({

    appButtonContainer: {
        elevation: 8,
        backgroundColor: "#009688",
        borderRadius: 10,
        paddingVertical: 10,
        paddingHorizontal: 12
      },
      appButtonText: {
        fontSize: 18,
        color: "#fff",
        fontWeight: "bold",
        alignSelf: "center",
        textTransform: "uppercase"
      },

      BottomButtomContainer:{
        // flex: 1,
        postion:"absolute",
        // bottom:100,
        // left:-27,
        alignItems: "center",
        justifyContent: 'center',
        backgroundColor: "transparent",
        bottom:'0%',
        height:'20%',
        // marginTop:'5%',
        // marginBottom:'5%',
        // borderWidth:3,
        marginTop:0,
        
        // paddingTop: 200,
      },

      BottomButtom: {
        width: "40%",
        borderRadius: 11,
        height: 45,
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "#030082",
        
        // marginBottom: 0
    },

      BottomButtomText:{
        fontSize:14,
        fontWeight:600,
        color:'white',
        alignItems: "center",
        justifyContent: "center",
        
      },

      BottomButtomCapText:{
        fontSize:12,
        fontWeight:600,
        color:'#515151',
        marginTop:10,
      },

      BottomButtomCapTextLink:{
        color:'#FF00F5'
      },

      CheckButtonTemp: {
        width: "70%",
        borderRadius: 11,
        height: 60,
        alignItems: "center",
        // justifyContent: "center",
        backgroundColor: "#A8FFD5",
        borderWidth:2,
        borderColor:'#7AFFBF',
        // marginBottom: 0
        flexDirection: 'row',
        margin:'2%'
    },
      CheckButtonPh: {
        width: "70%",
        borderRadius: 11,
        height: 60,
        alignItems: "center",
        // justifyContent: "center",
        backgroundColor: "#EAEAEA",
        borderWidth:2,
        borderColor:'#CECECE',
        // marginBottom: 0
        flexDirection: 'row',
        margin:'2%'
    },
      CheckButtonTurbidity: {
        width: "70%",
        borderRadius: 11,
        height: 60,
        alignItems: "center",
        // justifyContent: "center",
        backgroundColor: "#A8FFD5",
        borderWidth:2,
        borderColor:'#7AFFBF',
        // marginBottom: 0
        flexDirection: 'row',
        margin:'2%'
    },
      CheckButtonCndctvty: {
        width: "70%",
        borderRadius: 11,
        height: 60,
        alignItems: "center",
        // justifyContent: "center",
        backgroundColor: "#EAEAEA",
        borderWidth:2,
        borderColor:'#CECECE',
        // marginBottom: 0
        flexDirection: 'row',
        margin:'2%'
    },


    CheckButtonText:{
        fontSize:18,
        fontWeight:600,
        color:'#00397C',
        alignItems: "center",
        justifyContent: "center",
        lineHeight:15
      },

    HeadText:{
        position:"absolute",
        // fontFamily:"ubuntu",
        fontSize:23,
        fontWeight:"bold",
        color:"white",
        top:73,
        left:30,
      },

      CheckBtnTxtCap:{
          fontSize:13,
          color:'rgba(0, 59, 128, 0.77)',
      },  
      HeadTextCap:{
        position:"absolute",
