import React, { useEffect, useState } from 'react';
import { FlatList,Button, Text, View, StyleSheet,ActivityIndicator } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import Spinner from 'react-native-loading-spinner-overlay';
import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    
    StackedBarChart
  } from "react-native-chart-kit";

  import { Dimensions,TouchableHighlight, Image,TouchableOpacity, } from "react-native";

export default function App() {

  const [isLoading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  console.log(data);

  useEffect(() => {
    fetch('https://raw.githubusercontent.com/adhithiravi/React-Hooks-Examples/master/testAPI.json')
      .then((response) => response.json())
      .then((json) => setData(json))
      .catch((error) => console.error(error))
      .finally(() => setLoading(false));
  }, []);

  const dataPie = {
    labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
    data: [,0.4, 0.7, 0.8, 0.3]
  };

//   const dataPie = {
//     labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
//     data: [,data.ph, data.temp, data.turbidity, data.conductivity]
//   };


  return (

    <View style={{ flex: 1, padding: 0 }}>
      {isLoading ? <Text>Loading...</Text> : 
      (
          
          <View style={{ flex: 1, flexDirection: 'column', justifyContent:  'space-between'}}>

        <View style = {styles.headContainer}>
            <LinearGradient
                // Button Linear Gradient
                colors={['#020058', '#030096']} 
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style={styles.headContent}
                >
                <Text style={styles.HeadText}>Quality Test</Text>
                <Text style={styles.HeadTextCap}>Water quality results</Text>
            </LinearGradient>
        </View>

          <View style = {styles.InnerHeadContent}>
                
            <Text style = {styles.InnerHeadText}>Start water test</Text>

          </View>

          <View style = {styles.Instruction}>
      
          
            
          </View>

          <View style = {styles.BottomButtomContainer}>
            <TouchableOpacity style={styles.BottomButtom} >
              <Text style={styles.BottomButtomText}>Login</Text>
            </TouchableOpacity>
            <Text style={styles.BottomButtomCapText}>How to use this device? <Text style={styles.BottomButtomCapTextLink}>More info</Text></Text>
          </View>

            
        </View>
      )}
    </View>
  );
};

  const AppButton = ({ onPress, title }) => (
    <TouchableOpacity onPress={onPress} style={styles.appButtonContainer}>
      <Text style={styles.appButtonText}>{title}</Text>
    </TouchableOpacity>
  );

  const MyActivityIndicator = () => {
	return (
      	<View style={{ flex: 1, justifyContent: "center"}}>
      		//size can be "small" or "large"
			<ActivityIndicator size="large" color="#00ff00" />
      	</View>
    );
}

// export default MyActivityIndicator;

  const styles = StyleSheet.create({

    appButtonContainer: {
        elevation: 8,
        backgroundColor: "#009688",
        borderRadius: 10,
        paddingVertical: 10,
        paddingHorizontal: 12
      },
      appButtonText: {
        fontSize: 18,
        color: "#fff",
        fontWeight: "bold",
        alignSelf: "center",
        textTransform: "uppercase"
      },

      BottomButtomContainer:{
        // flex: 1,
        postion:"absolute",
        // bottom:100,
        // left:-27,
        alignItems: "center",
        justifyContent: 'center',
        backgroundColor: "transparent",
        bottom:'0%',
        height:'20%',
        // marginTop:'5%',
        // marginBottom:'5%',
        // borderWidth:3,
        marginTop:0,
        
        // paddingTop: 200,
      },

      BottomButtom: {
        width: "40%",
        borderRadius: 11,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "#030082",
        // marginBottom: 0
    },

      BottomButtomText:{
        fontSize:14,
        fontWeight:600,
        color:'white',

      },

      BottomButtomCapText:{
        fontSize:12,
        fontWeight:600,
        color:'#515151',
        marginTop:10,
      },

      BottomButtomCapTextLink:{
        color:'#FF00F5'
      },
    HeadText:{
        position:"absolute",
        // fontFamily:"ubuntu",
        fontSize:20,
        fontWeight:"bold",
        color:"white",
        top:73,
        left:30,
      },
  
      HeadTextCap:{
        position:"absolute",
        fontSize:12,
        // fontWeight:"bold",
        color:"#D1E1FF",
        top:103,
        left:30,
      },

      container: {
        flex: 1,
        alignItems: "center",
        justifyContent: 'flex-end',
        backgroundColor: "#EFFBFF",
    },
    headContainer: {
        flex: 1,
        position:"absolute",
        width:Dimensions.get("window").width,
        height:"23%",
    },
    headContent: {
        flex: 1,
        // width:Dimensions.get("window").width,
        // height: "10%",
    },

    

    InnerHeadContent:{
        // flex: 1,
        postion:"absolute",
        // bottom:100,
        // left:-27,
        alignItems: "center",
        justifyContent: 'center',
        backgroundColor: "transparent",
        top:'25%',
        height:'10%',
        marginTop:'5%',
        marginBottom:'5%',
        // borderWidth:3,
        
        // paddingTop: 200,
      },

      InnerHeadText:{
        fontSize:24,
        fontWeight:'bold',
        color:'#00397C',
        postion:'absolute',
        bottom:200,
      },

      Instruction:{
         // flex: 1,
         postion:"absolute",
         // bottom:100,
         // left:-27,
         alignItems: "center",
         justifyContent: 'center',
         backgroundColor: "transparent",
         bottom:'-10%',
         height:'40%',
         marginRight:0,
         marginTop:'5%',
        //  marginBottom:'5%',
        //  borderWidth:3,
         
         // paddingTop: 200,
      },


      InstructionNum: {
        postion:'absolute',
        flexDirection: 'row',
        width:'80%',
        // borderWidth: 1,
        padding: 27,
        paddingBottom:15,
        paddingTop:10,
    
        borderColor: 'rgba(0, 0, 0, 0.5)',
        backgroundColor: 'white'
     },
    

     InstructionText:{
      color:'rgba(81, 81, 81, 0.86)',
      fontSize:17,
      fontWeight:'700',
     },

     StartLogo:{
      // justifyContent: 'center',
      // alignItems: 'center',
      width:20,
      height:20,
      marginRight:10,
      marginTop:10
  },

});
