import React, { useEffect, useState } from 'react';
import { FlatList,Button, Text, View, StyleSheet,ActivityIndicator } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import Spinner from 'react-native-loading-spinner-overlay';
import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    StackedBarChart
  } from "react-native-chart-kit";

  import { Dimensions,TouchableHighlight, Image } from "react-native";

export default function App() {

  const [isLoading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  console.log(data);

  useEffect(() => {
    fetch('https://raw.githubusercontent.com/adhithiravi/React-Hooks-Examples/master/testAPI.json')
      .then((response) => response.json())
      .then((json) => setData(json))
      .catch((error) => console.error(error))
      .finally(() => setLoading(false));
  }, []);

  const dataPie = {
    labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
    data: [,0.4, 0.7, 0.8, 0.3]
  };

//   const dataPie = {
//     labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
//     data: [,data.ph, data.temp, data.turbidity, data.conductivity]
//   };


  return (

    <View style={{ flex: 1, padding: 0 }}>
      {isLoading ? <Text>Loading...</Text> : 
      (
          
          <View style={{ flex: 1, flexDirection: 'column', justifyContent:  'space-between'}}>

        <View style = {styles.headContainer}>
            <LinearGradient
                // Button Linear Gradient
                colors={['#020058', '#030096']} 
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style={styles.headContent}
                >
                <Text style={styles.HeadText}>Quality Test</Text>
                <Text style={styles.HeadTextCap}>Water quality results</Text>
            </LinearGradient>
        </View>

          <View style = {styles.InnerHeadContent}>
                
            <Text style = {styles.InnerHeadText}>Start water test</Text>

          </View>

            <View style = {styles.Instruction}>
        
              <View style = {styles.Instruction1}> 
                <Image source={require('../assets/number-1-circle.png')} style = {styles.StartLogo} />
                <Text style = {styles.InstructionText}>
                  Get the water sample, 
                  using of a beaker is reccomended 
                </Text>
              </View>
              <View style = {styles.Instruction2}> 
              <Image source={require('../assets/number-2-circle.png')} style = {styles.StartLogo} />
                  <Text style = {styles.InstructionText}>
                    Prepare a clear space to test the water 
                  </Text>
              </View>
              <View style = {styles.Instruction3}> 
              <Image source={require('../assets/number-3-circle.png')} style = {styles.StartLogo} />
                  <Text style = {styles.InstructionText}>
                    make sure all the sensors
                    are  in water 
                  </Text>
              </View>
              
            </View>

            
        </View>
      )}
    </View>
  );
};

  const AppButton = ({ onPress, title }) => (
    <TouchableOpacity onPress={onPress} style={styles.appButtonContainer}>
      <Text style={styles.appButtonText}>{title}</Text>
    </TouchableOpacity>
  );

  const MyActivityIndicator = () => {
	return (
      	<View style={{ flex: 1, justifyContent: "center"}}>
      		//size can be "small" or "large"
			<ActivityIndicator size="large" color="#00ff00" />
      	</View>
    );
}

// export default MyActivityIndicator;

  const styles = StyleSheet.create({

    appButtonContainer: {
        elevation: 8,
        backgroundColor: "#009688",
        borderRadius: 10,
        paddingVertical: 10,
        paddingHorizontal: 12
      },
      appButtonText: {
        fontSize: 18,
        color: "#fff",
        fontWeight: "bold",
        alignSelf: "center",
        textTransform: "uppercase"
      },

    HeadText:{
        position:"absolute",
        // fontFamily:"ubuntu",
        fontSize:20,
        fontWeight:"bold",
        color:"white",
        top:73,
        left:30,
      },
  
      HeadTextCap:{
        position:"absolute",
        fontSize:12,
        // fontWeight:"bold",
        color:"#D1E1FF",
        top:103,
        left:30,
      },

      container: {
        flex: 1,
        alignItems: "center",
        justifyContent: 'flex-end',
        backgroundColor: "#EFFBFF",
    },
    headContainer: {
        flex: 1,
        position:"absolute",
        width:Dimensions.get("window").width,
        height:"23%",
    },
    headContent: {
        flex: 1,
        // width:Dimensions.get("window").width,
        // height: "10%",
    },

    InnerHeadContent:{
        // flex: 1,
        postion:"absolute",
        // bottom:100,
        // left:-27,
        alignItems: "center",
        justifyContent: 'center',
        backgroundColor: "transparent",
        top:'20%',
        height:'10%',
        marginTop:'5%',
        marginBottom:'5%',
        borderWidth:3,
        
        // paddingTop: 200,
      },

      InnerHeadText:{
        fontSize:20,
        fontWeight:'bold',
        color:'#00397C',
        postion:'absolute',
        bottom:200,
      },

      Instruction:{
         // flex: 1,
         postion:"absolute",
         // bottom:100,
         // left:-27,
         alignItems: "center",
         justifyContent: 'center',
         backgroundColor: "transparent",
         bottom:'22%',
         height:'40%',
         marginTop:'5%',
         marginBottom:'5%',
         borderWidth:3,
         
         // paddingTop: 200,
      },


      Instruction1: {
        postion:'absolute',
        flexDirection: 'row',
        width:'90%',
        // borderWidth: 1,
        padding: 25,
        paddingBottom:10,
        paddingTop:10,
    
        borderColor: 'rgba(0, 0, 0, 0.5)',
        backgroundColor: 'white'
     },
     Instruction2: {
        postion:'absolute',
        flexDirection: 'row',
        width:'90%',
        // borderWidth: 1,
        padding: 25,
        paddingBottom:10,
        paddingTop:10,
    
        borderColor: 'rgba(0, 0, 0, 0.5)',
        backgroundColor: 'white'
     },
     Instruction3: {
        postion:'absolute',
        flexDirection: 'row',
        width:'90%',
        // borderWidth: 1,
        padding: 25,
        paddingBottom:10,
        paddingTop:10,
      
        borderColor: 'rgba(0, 0, 0, 0.5)',
        backgroundColor: 'white'
     },

     InstructionText:{
      color:'rgba(81, 81, 81, 0.86)',
      fontSize:17,
      fontWeight:'700',
     },

     StartLogo:{
      // justifyContent: 'center',
      // alignItems: 'center',
      width:20,
      height:20,
      marginRight:10,
      marginTop:10
  },

});
