import { ADD_USER } from "../constants";

const initialState = {
  user: "mahen",
};

export default function (state = initialState, action) {
  switch (action.type) {
    case ADD_USER: {
      return {
        ...state,
        user: action.payload,
      };
    }

    default:
      return state;
  }
}
