import React, { useEffect, useState } from 'react';
import { FlatList,Button, Text, View, StyleSheet,ActivityIndicator } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import Spinner from 'react-native-loading-spinner-overlay';
// import TestsResults from '../assets/TestsResults.json';
import * as data from '../assets/TestsResults.json';
import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    StackedBarChart
  } from "react-native-chart-kit";

  import { Dimensions,TouchableHighlight  } from "react-native";

  export default function Water({navigation}) {

    
  // const [isLoading, setLoading] = useState(true);
  // const [data, setData] = useState([]);
  // console.log(data);

  
  // useEffect(() => {
  //   fetch(TestsResults)
  //     .then((response) => response.json())
  //     .then((json) => setData(json))
  //     .catch((error) => console.error(error))
  //     .finally(() => setLoading(false));
  // }, []);

  const dataPie = {
    labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
    data: [,(data.value_params.ph*7.14)/100, data.value_params.temp/100, (data.value_params.turbidity*2)/100, (data.value_params.conduct*2)/100]
  };

//   const dataPie = {
//     labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
//     data: [,data.ph, data.temp, data.turbidity, data.conductivity]
//   };


  return (

    <View style={{ flex: 1, padding: 0 }}>
      {/* {isLoading ? <Text>Loading...</Text> :  */}         
          <View style={{ flex: 1, flexDirection: 'column', justifyContent:  'space-between'}}>


        
        <View style = {styles.headContainer}>
            <LinearGradient
                // Button Linear Gradient
                colors={['#020058', '#030096']}
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style={styles.headContent}
                >
                <Text style={styles.HeadText}>Quality test</Text>
                <Text style={styles.HeadTextCap}>Water quality results</Text>
            </LinearGradient>
        </View>

        <View style = {styles.StatusBarContainer}>
          <TouchableHighlight>
              <Text style = {styles.StatusBar}>
              <Text style={styles.Measurement}>Conductivity</Text><br/> 
                {data.value_params.conduct} (S/m)
              </Text>
          </TouchableHighlight>
        </View>

        <View style = {styles.pieChart}>
                {/* <Text style = {styles.LocationText}><Text style={styles.Measurement}>Location:</Text>   {data.location}</Text> */}
                <ProgressChart
                data={dataPie}
                width={Dimensions.get("window").width}
                height={220}
                strokeWidth={12}
                radius={27}
                chartConfig={{
                    backgroundColor: "transparent",
                    backgroundGradientFrom: "transparent",
                    backgroundGradientTo: "transparent",
                    decimalPlaces: 2, // optional, defaults to 2dp
                    color: (opacity = 1) => `rgba(3, 0, 150, ${opacity})`,
                    labelColor: (opacity = 1) => `rgba(2, 0, 88, ${opacity})`,
                    style: {
                    borderRadius: 16,
                    },
                    propsForDots: {
                    r: "6",
                    strokeWidth: "2",
                    stroke: "#fff"
                    }
                }}
                hideLegend={false}
                />

        <View style = {styles.PhTempContainer}>
          <TouchableHighlight>
              <Text style = {styles.PhButton}>
              <Text style={styles.Measurement}>pH</Text><br/>
                {data.value_params.ph}
              </Text>
          </TouchableHighlight>
          <TouchableHighlight>
              <Text style = {styles.TempButton}>
              <Text style={styles.Measurement}>Temp</Text><br/>
                {data.value_params.temp}Â°C
              </Text>
          </TouchableHighlight>
        </View>
  
        <View style = {styles.TurbidityCndctContainer}>
          <TouchableHighlight>
              <Text style = {styles.TurbidityButton}>
              <Text style = {styles.Measurement}>Turbidity</Text><br/>
                {data.value_params.turbidity} Ntu
              </Text>
          </TouchableHighlight>
          <TouchableHighlight>
              <Text style = {styles.CondctButton}>
              <Text style={styles.Measurement}>Conductivity</Text><br/> 
                {data.value_params.conduct} (S/m)
              </Text>
          </TouchableHighlight>
        </View>
    

                <Text> </Text>
                {/* <Text style = {styles.LocationText}><Text style={styles.Measurement}>Water quality:</Text> {data.predicted_water_type}</Text> */}

            </View>  
        </View>
      )
    </View>
  );
};

  // add this to a button
  const AppButton = ({ onPress, title }) => (
    <TouchableOpacity onPress={onPress} style={styles.appButtonContainer}>
      <Text style={styles.appButtonText}>{title}</Text>
    </TouchableOpacity>
  );

  const MyActivityIndicator = () => {
	return (
      	<View style={{ flex: 1, justifyContent: "center"}}>
      		//size can be "small" or "large"
			<ActivityIndicator size="large" color="#00ff00" />
      	</View>
    );
}

// export default MyActivityIndicator;

  const styles = StyleSheet.create({

    appButtonContainer: {
        elevation: 8,
        backgroundColor: "#009688",
        borderRadius: 10,
        paddingVertical: 10,
        paddingHorizontal: 12
      },
      appButtonText: {
        fontSize: 18,
        color: "#fff",
        fontWeight: "bold",
        alignSelf: "center",
        textTransform: "uppercase"
      },

      Measurement: {
        // fontSize:16,
        fontWeight:'bold',
      },

      HeadText:{
        position:"absolute",
        // fontFamily:"ubuntu",
        fontSize:22,
        fontWeight:"bold",
        color:"white",
        top:83,
        left:30,
      },
  
      HeadTextCap:{
        position:"absolute",
        fontSize:12,
        // fontWeight:"bold",
        color:"#D1E1FF",
        top:113,
        left:30,
      },

      container: {
        flex: 1,
        alignItems: "center",
        justifyContent: 'flex-end',
        backgroundColor: "#EFFBFF",
    },

    PhButtonContainer:{
      // flex: 1,
      postion:"absolute",
      bottom:0,
      // top:170,
      // left:-80,
      alignItems: "center",
      // justifyContent: 'flex-end',
      backgroundColor: "transparent",
      marginTop: 0
      // paddingTop: 200,
    },
    TempButtonContainer:{
      // flex: 1,
      postion:"absolute",
      // bottom:-70,
      // left:-80,
      alignItems: "center",
      // justifyContent: 'flex-end',
      backgroundColor: "transparent",
      marginTop: 0
      // paddingTop: 200,
    },
    TurbidityButtonContainer:{
      // flex: 1,
      postion:"absolute",
      // bottom:0,
      // bottom:70,
      // left:72,
      alignItems: "center",
      // justifyContent: 'flex-end',
      backgroundColor: "transparent",
      marginTop: 0
      // paddingTop: 200,
    },
    CondctButtonContainer:{
      // flex: 1,
      postion:"absolute",
      // bottom:0,
      // bottom:70,
      // left:72,
      alignItems: "center",
      // justifyContent: 'flex-end',
      backgroundColor: "transparent",
      marginTop: 0
      // paddingTop: 200,
    },

    StatusBarContainer:{
      postion:"absolute",
      bottom:0,
      top:200,
      alignItems: "center",
      justifyContent: 'flex-end',
      backgroundColor: "transparent",
      marginTop: 0
    },

    StatusBar:{
      postion:'absolute',
      width:300,
      borderWidth: 3,
      padding: 25,
      borderRadius:10,
      // borderTopWidth:0,
      // borderLeftWidth:0,
      borderColor: 'rgba(0, 0, 0, 0.5)',
      backgroundColor: 'white'
    },

    headContainer: {
        flex: 1,
        position:"absolute",
        width:Dimensions.get("window").width,
        height:"20%",
    },
    headContent: {
        flex: 1,
        // width:Dimensions.get("window").width,
        // height: "10%",
    },

    PhButton: {
      postion:'absolute',
      width:150,
      borderWidth: 1,
      padding: 25,
      borderTopWidth:0,
      borderLeftWidth:0,
      borderColor: 'rgba(0, 0, 0, 0.5)',
      backgroundColor: 'white'
   },
    TempButton: {
      postion:'absolute',
      width:150,
      borderWidth: 1,
      padding: 25,
      borderBottomWidth:0,
      borderLeftWidth:0,
      borderColor: 'rgba(0, 0, 0, 0.5)',
      backgroundColor: 'white'
   },
    TurbidityButton: {
      postion:'absolute',
      width:150,
      borderWidth: 1,
      padding: 25,
      borderTopWidth:0,
      borderRightWidth:0,
      borderColor: 'rgba(0, 0, 0, 0.5)',
      backgroundColor: 'white'
   },
    CondctButton: {
      postion:'absolute',
      width:150,
      borderWidth: 1,
      padding: 25,
      borderBottomWidth:0,
      borderRightWidth:0,
      borderColor: 'rgba(0, 0, 0, 0.5)',
      backgroundColor: 'white'
   },

    pieChart:{
        // flex: 1,
        postion:"absolute",
        top:100,
        left:-27,
        alignItems: "center",
        justifyContent: 'flex-end',
        backgroundColor: "transparent",
        marginTop: 100,
        // paddingTop: 200,
      },

});
