import {ADD_USER} from './constants';

const initialState = {
    user: null,
}

export const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_USER: 
            
            return {
                ...state,
                user: state.user,
            }

        default:
            return state;
    }
}
