                </TouchableHighlight>
                <TouchableHighlight>
                  <Text style={styles.StatusBar}>
                    <Text style={styles.Measurement}>pH</Text>
                    <br />
                    {data.value_params.ph}
                  </Text>
                </TouchableHighlight>
              </View>
              <View style={styles.StatusBarContainer2}>
                <TouchableHighlight>
                  <Text style={styles.StatusBarLeft}>
                    <Text style={styles.Measurement}>Conductivity</Text>
                    <br />
                    {data.value_params.conduct} (S/m)
                  </Text>
                </TouchableHighlight>
                <TouchableHighlight>
                  <Text style={styles.StatusBar}>
                    <Text style={styles.Measurement}>Turbidity</Text>
                    <br />
                    {data.value_params.turbidity} Ntu
                  </Text>
                </TouchableHighlight>
              </View>
            </View>
            <View style={styles.locationBlock}>
              <Image
                source={require("../assets/location-pin-red.png")}
                style={styles.locationPin}
              />
              <Text style={styles.locationTxt}>{data.location}</Text>
            </View>
          </LinearGradient>
        </View>

        <View style={styles.previousContainer}>
