import React, { useEffect, useState } from "react";
import {
  FlatList,
  Button,
  Text,
  View,
  StyleSheet,
  ActivityIndicator,
  TextInput,
  TouchableOpacity,
  Alert,
} from "react-native";
import { LinearGradient } from "expo-linear-gradient";
import TestingSensorPage from "./TestingSensorPage";
import Spinner from "react-native-loading-spinner-overlay";
import * as Animatable from "react-native-animatable";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import Feather from "react-native-vector-icons/Feather";
import { useSelector, useDispatch } from "react-redux";
import { ADD_USER } from "../store/actions";

import {
  LineChart,
  BarChart,
  PieChart,
  ProgressChart,
  ContributionGraph,
  StackedBarChart,
} from "react-native-chart-kit";

import { Dimensions, TouchableHighlight, Image } from "react-native";

export default function LoginPage({ navigation }) {
  //   const dataPie = {
  //     labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
  //     data: [,data.ph, data.temp, data.turbidity, data.conductivity]
  //   };
  const [text, onChangeText] = React.useState("location ");

  const reduxState = useSelector((state) => state.initialReducer.user);

  const dispatch = useDispatch();

  const [data, setData] = React.useState({
    email: "",
    password: "",
    check_textInputChange: false,
    secureTextEntry: true,
    isValidPassword: true,
    isValidUser: true,
  });

  const textInputChange = (val) => {
    if (val.trim().length >= 4) {
      setData({
        ...data,
        email: val,
        check_textInputChange: true,
        isValidUser: true,
      });
    } else {
      setData({
        ...data,
        email: val,
        check_textInputChange: false,
        isValidUser: false,
      });
    }
  };
  const handelPasswordChange = (val) => {
    if (val.trim().length >= 6) {
      setData({
        ...data,
        password: val,
        isValidPassword: true,
      });
    } else {
      setData({
        ...data,
        password: val,
        isValidPassword: false,
      });
    }
  };

  const updateSecuretextEntry = () => {
    setData({
      ...data,
      secureTextEntry: !data.secureTextEntry,
    });
  };
  const pressHandler = () => {
    navigation.navigate("Home");
  };

  const pressHandler2 = () => {
    navigation.navigate("Signup");
  };

  const handleValidUser = (val) => {
    if (val.trim().length >= 4) {
      setData({
        ...data,
        isValidUser: true,
      });
    } else {
      setData({
        ...data,
        isValidUser: false,
      });
    }
  };
  const loginHandle = (email, password) => {
    if (data.email.length >= 4 && data.password.length >= 6) {
      // navigation.navigate('Home');
      username: data.email;
      password: data.password;
      const req = {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          user_name: data.email,
          password: data.password,
          // tele_no: data.contact_no,
          // location: selectedValue,
          // nic_no : data.NIC,
          // email : data.email,
          // user_f_name : "Meraj",
          // user_l_name : "Vindira",
          // admin : false
        }),
      };

      console.log(data.email);

      var errorAlert = "";

      fetch("http://127.0.0.1:5000/login", req)
        .then((response) => response.json())
        .then((response) => {
          console.log(response["user_details"]["user_name"]);
          if (response["message"] == "Unauthorized") {
            errorAlert = "Username or password doesn't match";
            var user_name = response["user_details"]["user_name"];
            dispatch({
              type: ADD_USER,
              payload: user_name,
          });
          }
          if (response["message"] == "Authorized") {
            navigation.navigate("Dashboard");
          }
        });
    } else if (data.email.length == 0 || data.password.length == 0) {
      alert("Please fill the following requirments");
    }
  };

  return (
    <View style={{ flex: 1, padding: 0, backgroundColor: "white" }}>
      {/* {isLoading ? <ActivityIndicator/> : ( */}

      <View
        style={{
          flex: 1,
          flexDirection: "column",
          justifyContent: "space-between",
        }}
      >
        {/* <View style = {styles.PageCotent}> */}
        {/* <View style = {styles.InnerHeadContent}>
                <Text style = {styles.InnerHeadText}>Welcome to<br/><Text style = {{color:"#0099C9"}}>JalaRead</Text>, mobile</Text>
                <Text style = {styles.InstructionText}>
                Your water testing deviceses, mobile <br/>application on your fingertips
                </Text>
            </View> */}
        <View style={styles.headContainer}>
          <View>
            <TouchableOpacity
              onPress={() => navigation.navigate("WelcomePage")}
            >
              <Image
                source={require("../assets/back-arrow-blue.png")}
                style={styles.BackArrow}
              />
            </TouchableOpacity>

            <Text style={styles.HeadText}>JalaRead</Text>
          </View>
        </View>

        <View style={styles.Instruction}>
          <Image
            source={require("../assets/login-page-img.svg")}
            style={styles.LoginPageImg}
          />
          <Text style={styles.LoginHeadTxt}>
            <Text style={styles.LoginHeadTxtCap}>Login</Text>
            <br />
            Welcome Back!
          </Text>

          <View
            style={{
              marginTop: 290,
              position: "absolute",
              width: "100%",
              height: "50%",
            }}
          >
            <Animatable.View animation="fadeInUpBig" style={styles.footer}>
              <Text style={styles.text_footer}>Username</Text>
              <View style={styles.action}>
                <FontAwesome name="user-o" color="#05375a" size={20} />

                <TextInput
                  placeholder="Enter Your Username"
                  style={styles.textInput}
                  autoCapitalize="none"
                  onChangeText={(val) => textInputChange(val)}
                  onEndEditing={(e) => handleValidUser(e.nativeEvent.text)}
                />
                {data.check_textInputChange ? (
                  <Animatable.View animation="bounceIn">
                    <Feather
                      name="check-circle"
                      color="#37E290"
                      size={20}
                      style={{ position: "absolute", right: -150 }}
                    />
                  </Animatable.View>
                ) : null}
              </View>
              {data.isValidUser ? null : (
                <Animatable.View animation="fadeInLeft" duration={500}>
                  <Text style={styles.errorMsg}>
                    Username must be more than 4 characters.
                  </Text>
                </Animatable.View>
              )}

              <Text
                style={[
                  styles.text_footer,
                  {
                    marginTop: 35,
                  },
                ]}
              >
                Password
              </Text>

              <View style={styles.action}>
                <Feather name="lock" color="#05375a" size={20} />

                <TextInput
                  placeholder="Enter Your Password"
                  secureTextEntry={data.secureTextEntry ? true : false}
                  style={styles.textInput}
                  autoCapitalize="none"
                  onChangeText={(val) => handelPasswordChange(val)}
                />
                <TouchableOpacity onPress={updateSecuretextEntry}>
                  {data.secureTextEntry ? (
                    <Feather
                      name="eye-off"
                      color="grey"
                      size={20}
                      style={{ position: "absolute", right: -150 }}
                    />
                  ) : (
                    <Feather
                      name="eye"
                      color="grey"
                      size={20}
                      style={{ position: "absolute", right: -150 }}
                    />
                  )}
                </TouchableOpacity>
              </View>
              {data.isValidPassword ? null : (
                <Animatable.View animation="fadeInLeft" duration={500}>
                  <Text style={styles.errorMsg}>
                    Password must be more than 6 characters.
                  </Text>
                </Animatable.View>
              )}
            </Animatable.View>
          </View>
        </View>
        <View style={styles.BottomButtomContainer}>
          <TouchableOpacity
            style={styles.BottomButtom}
            onPress={() => {
              loginHandle(data.email, data.password);
            }}
          >
            <Text style={styles.BottomButtomText}>Login</Text>
          </TouchableOpacity>
          <Text style={styles.BottomButtomCapText}>
            Don't have an account?{" "}
            <TouchableOpacity
              style={{ color: "#FF7B8A" }}
              onPress={() => navigation.navigate("SignUpPage")}
            >
              Sign in
            </TouchableOpacity>
          </Text>
        </View>

        {/* </View> */}
      </View>
      {/* )} */}
    </View>
  );
}

