import React, { useEffect, useState } from 'react';
import { FlatList,Button, Text, View, StyleSheet,ActivityIndicator,Image, TouchableOpacity } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import Spinner from 'react-native-loading-spinner-overlay';
import TestsResults from '../assets/use_results.json';
import * as data from '../assets/TestsResults.json';
import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    StackedBarChart
  } from "react-native-chart-kit";

  import { Dimensions,TouchableHighlight  } from "react-native";

  export default function MonthlyResults({navigation}) {

 

const [currentDate, setCurrentDate] = useState('');
const [currentMonth, setCurrentMonth] = useState('');

  useEffect(() => {
    var monthNames = [ 'JAN', 'FEB', 'MAR', 'APR', 'MAY','JUN',
    'JUL', 'AUG', 'SEP', 'OCT', 'NOV', 'DEC'];
    var date = new Date().getDate(); //Current Date
    var month = monthNames[new Date().getMonth()]; //Current Month
    // var year = new Date().getFullYear(); //Current Year
    // var hours = new Date().getHours(); //Current Hours
    // var min = new Date().getMinutes(); //Current Minutes
    // var sec = new Date().getSeconds(); //Current Seconds
    setCurrentDate(
      date 
    );
    setCurrentMonth(
      month 
    );
  }, []);

  const getWaterType = (waterType) => {
    if(waterType == "Excellent"){
      return 1;
    }
    if(waterType == "Normal"){
      return 2;
    }
    if(waterType == "Bad"){
      return 3;
    }
  }

  const [isLoading, setLoding] = useState(true);
  const [data,setData] = useState([]);
  const [title,setTitle] = useState([]);
  const [loading,setLoading] = useState(false);
  const [description,setDescription] = useState([]);
  console.log(data)

  useEffect(() => {
    fetch("https://raw.githubusercontent.com/adhithiravi/React-Hooks-Examples/master/testAPI.json")
    .then((response) => response.json())
    .then((json) => setData(json))
    .catch((error) => alert(error))
    .finally(() => setLoding(false));
  }, []);

  

  return (

    <View style={{ flex: 1, padding: 0, backgroundColor:"white" }}>
      {isLoading ? <Text>Loading...</Text> :          
          <View style={{ flex: 1, flexDirection: 'column', justifyContent:  'space-between'}}>


        
        <View style = {styles.headContainer}>
            <LinearGradient
                // Button Linear Gradient
                colors={['#EEF2FF', '#ffffff']}
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style={styles.headContent}
                >
                <TouchableOpacity onPress={() => navigation.navigate('Dashboard')}>
                  <Image source={require('../assets/back-arrow-blue.png')} style = {styles.BackArrow}  />
                </TouchableOpacity>
                <Text style={styles.HeadText}>Quality test</Text>
                <Text style={styles.HeadTextCap}>Water quality results</Text>
                <Image source={require('../assets/calendar-blue.png')} style = {styles.CalendarImg} />
            </LinearGradient>
        </View>

        <View style = {styles.StatusContainer} >
            <LinearGradient
                // Button Linear Gradient
                colors={['#EEF2FF', '#ffffff']}
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style={styles.headContent}
                >
                <View style = {styles.resultsContainer}>
                <FlatList
                style = {{}}
                      data={data.articles}
                      keyExtractor={({ id } , index)=> id}
                      renderItem={({ item }) =>(
                        <View> 
                          <Text>
                        {item.title},
                        {item.releaseYear}

                      </Text>
                      </View>

                      ) }
                    />
                    <View style = {styles.previousContainer}>
                        <Image source={require('../assets/select-arrow-blue.png')} style = {styles.SelectArrow} /> 
                        <Text style={styles.slctCntnrTxt}>Previous results</Text>
                        <Text style={styles.slctCntnrTxtCap}>Water quality results</Text>
                        <Text style={[
                          
                          (getWaterType(data.predicted_water_type) == 1 ) ? styles.excellentQlt:styles.selectDate,
                          (getWaterType(data.predicted_water_type) == 2 ) ? styles.normalQlt:styles.selectDate,
                          (getWaterType(data.predicted_water_type) == 3 ) ? styles.badQlt:styles.selectDate,
                          
                          styles.selectDate]}>{currentDate}<br/><Text style={{fontSize:11, fontWeight:500}}>{currentMonth}</Text></Text>
                    </View>
                </View>
            </LinearGradient>
        </View>
  
        </View>
  }
    </View>
  );
};

  // add this to a button
  const AppButton = ({ onPress, title }) => (
    <TouchableOpacity onPress={onPress} style={styles.appButtonContainer}>
      <Text style={styles.appButtonText}>{title}</Text>
    </TouchableOpacity>
  );

  const MyActivityIndicator = () => {
	return (
      	<View style={{ flex: 1, justifyContent: "center"}}>
      		//size can be "small" or "large"
			<ActivityIndicator size="large" color="#00ff00" />
      	</View>
    );
}

// export default MyActivityIndicator;
let deviceWidth = Dimensions.get('window').width

  const styles = StyleSheet.create({

    appButtonContainer: {
        elevation: 8,
        backgroundColor: "#009688",
        borderRadius: 10,
        paddingVertical: 10,
        paddingHorizontal: 12
      },
      appButtonText: {
        fontSize: 18,
        color: "#fff",
        fontWeight: "bold",
        alignSelf: "center",
        textTransform: "uppercase"
      },

      BackArrow:{
        // justifyContent: 'center',
        // alignItems: 'center',
        width:10,
        height:17,
