import React from 'react';
import { 
  View, 
  Text, 
  TouchableOpacity, 
  TextInput,
  Platform,
  StyleSheet ,
  StatusBar,
  Alert,
  Button
} from 'react-native';
import * as Animatable from 'react-native-animatable';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import Feather from 'react-native-vector-icons/Feather';
import { useTheme } from 'react-native-paper';
export default function Loginex({navigation}) {

  const [data,setData] =React.useState({
    email:'',
    password:'',
    check_textInputChange:false,
    secureTextEntry:true,
    isValidPassword:true,
    isValidUser:true
    
  });

  const textInputChange = (val)=>{
    if(val.trim().length>=4){
      setData({
        ...data,
        email:val,
        check_textInputChange:true,
        isValidUser:true
      });
    }
    else{
        setData({
          ...data,
          email:val,
          check_textInputChange:false,
          isValidUser:false
        });
    }
  }
  const handelPasswordChange = (val) => {
    if(val.trim().length>=6){
      setData({
        ...data,
        password:val,
        isValidPassword:true
      });
    }
    else{
      setData({
        ...data,
        password:val,
        isValidPassword:false
      });
    }
  
  }

  const updateSecuretextEntry = () => {
    setData({
      ...data,
      secureTextEntry:!data.secureTextEntry
    });
  }
  const pressHandler =() =>
    { navigation.navigate('Home');
  }
   

const pressHandler2 =() =>{
  navigation.navigate('Signup');
}

const handleValidUser=(val) =>{
  if(val.trim().length>=4){
    setData({
      ...data,
      isValidUser:true
    });
  }
  else{
    setData({
      ...data,
      isValidUser:false
    });
   
  }
}
const loginHandle = (email, password) => {
  
if ( data.email.length >= 4 && data.password.length >= 6 ) {
      navigation.navigate('Home');
  }

  else if(data.email.length==0||data.password.length==0){
    alert("Please fill the following requirments");
  }

  
}
  return (
    <View style={styles.container}>
      <StatusBar backgroundColor="#009387" barStyle='light-content'/>
      <View style={styles.header}>
        <Text style={styles.text_header}>Welcome!</Text>
        </View>
        <Animatable.View 
        animation='fadeInUpBig'
        style={styles.footer}>
        <Text style={styles.text_footer}>Username</Text>
        <View style={styles.action}>
          <FontAwesome 
           name="user-o" 
           color="#05375a" 
           size={20}
          />      

         <TextInput 
          placeholder="Enter Your Username" 
          style={styles.textInput} 
           autoCapitalize="none"
           onChangeText={(val) =>textInputChange(val)}
           onEndEditing={(e) => handleValidUser(e.nativeEvent.text)}
         />
          {data.check_textInputChange? 
         <Animatable.View animation="bounceIn">
    
         <Feather 
              name="check-circle" 
              color="green"
              size={20}
         />
         </Animatable.View>
         :null}
          </View>
          {data.isValidUser ? null:
          <Animatable.View animation='fadeInLeft' duration={500}>
          <Text style={styles.errorMsg}>Username must be more than 4 characters.</Text>
          </Animatable.View>
          }
          
          <Text style={[styles.text_footer, {
            marginTop:35
            }]}>Password</Text>
        
          <View style={styles.action}>

          <Feather 
              name="lock" 
              color="#05375a"
              size={20}
         />
           
         <TextInput 
          placeholder="Enter Your Password" 
          secureTextEntry={data.secureTextEntry ? true : false}
          style={styles.textInput } 
           autoCapitalize="none"
           onChangeText={(val) =>handelPasswordChange(val)}
        
         />
        <TouchableOpacity onPress={
          updateSecuretextEntry
        }>
          {data.secureTextEntry ?
         <Feather 
              name="eye-off" 
              color="grey"
              size={20}
         />
         :
         <Feather 
         name="eye" 
         color="grey"
         size={20}
    />
          }
        </TouchableOpacity>
          </View>
          { data.isValidPassword ? null:
           <Animatable.View animation='fadeInLeft' duration={500}>
           <Text style={styles.errorMsg}>Password must be more than 6 characters.</Text>
           </Animatable.View>
          }
         
     <View style={styles.signinButton}>
   
      <Button title = 'Log In' onPress={()=>{loginHandle(data.email,data.password)}} />
      </View>
      <View style={styles.signupButton}>
      <Button title = 'Sign Up' onPress={pressHandler2}/>
      </View>
        </Animatable.View>
     
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    backgroundColor: '#000080'
  },
  signinButton: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: 18,
     fontWeight: 'bold'
  },
  signupButton: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: 18,
    fontWeight: 'bold'
  },

  header: {
      flex: 1,
      justifyContent: 'flex-end',
      paddingHorizontal: 20,
      paddingBottom: 50
  },
  footer: {
      flex: 3,
      backgroundColor: '#fff',
      borderTopLeftRadius: 30,
      borderTopRightRadius: 30,
      paddingHorizontal: 20,
      paddingVertical: 30
  },
  text_header: {
      color: '#fff',
      fontWeight: 'bold',
      fontSize: 30
  },
  text_footer: {
      color: '#05375a',
      fontSize: 18
  },
  action: {
      flexDirection: 'row',
      marginTop: 10,
      borderBottomWidth: 1,
      borderBottomColor: '#f2f2f2',
      paddingBottom: 5
  },
  actionError: {
      flexDirection: 'row',
      marginTop: 10,
      borderBottomWidth: 1,
      borderBottomColor: '#FF0000',
      paddingBottom: 5
  },
  textInput: {
      flex: 1,
      paddingLeft: 10,
      color: '#05375a',
  },
  errorMsg: {
      color: '#C70039',
      fontSize: 14,
  },
  button: {
      alignItems: 'center',
      marginTop: 50
  },
  signIn: {
      width: '100%',
      height: 50,
      justifyContent: 'center',
      alignItems: 'center',
      borderRadius: 10
  },
  textSign: {
      fontSize: 18,
      fontWeight: 'bold'
  }
});
