import React, { useEffect, useState } from 'react';
import { FlatList,Button, Text, View, StyleSheet,ActivityIndicator,Image } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import Spinner from 'react-native-loading-spinner-overlay';
// import TestsResults from '../assets/TestsResults.json';
import * as data from '../assets/TestsResults.json';
import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    StackedBarChart
  } from "react-native-chart-kit";

  import { Dimensions,TouchableHighlight  } from "react-native";

  export default function WaterTestingScreen({navigation}) {

    
  // const [isLoading, setLoading] = useState(true);
  // const [data, setData] = useState([]);
  // console.log(data);

  
  // useEffect(() => {
  //   fetch(TestsResults)
  //     .then((response) => response.json())
  //     .then((json) => setData(json))
  //     .catch((error) => console.error(error))
  //     .finally(() => setLoading(false));
  // }, []);

  const dataPie = {
    labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
    data: [,(data.value_params.ph*7.14)/100, data.value_params.temp/100, (data.value_params.turbidity*2)/100, (data.value_params.conduct*2)/100]
  };

//   const dataPie = {
//     labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
//     data: [,data.ph, data.temp, data.turbidity, data.conductivity]
//   };

const [currentDate, setCurrentDate] = useState('');
const [currentMonth, setCurrentMonth] = useState('');

  useEffect(() => {
    var monthNames = [ 'JAN', 'FEB', 'MAR', 'APR', 'MAY','JUN',
    'JUL', 'AUG', 'SEP', 'OCT', 'NOV', 'DEC'];
    var date = new Date().getDate(); //Current Date
    var month = monthNames[new Date().getMonth()]; //Current Month
    // var year = new Date().getFullYear(); //Current Year
    // var hours = new Date().getHours(); //Current Hours
    // var min = new Date().getMinutes(); //Current Minutes
    // var sec = new Date().getSeconds(); //Current Seconds
    setCurrentDate(
      date 
    );
    setCurrentMonth(
      month 
    );
  }, []);

  


  return (

    <View style={{ flex: 1, padding: 0 }}>
      {/* {isLoading ? <Text>Loading...</Text> :  */}         
          <View style={{ flex: 1, flexDirection: 'column', justifyContent:  'space-between'}}>


        
        <View style = {styles.headContainer}>
            <LinearGradient
                // Button Linear Gradient
                colors={['#EEF2FF', '#ffffff']}
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style={styles.headContent}
                >
                <Image source={require('../assets/back-arrow-blue.png')} style = {styles.BackArrow} />

                <Text style={styles.HeadText}>Hello,<br/>Jane Cooper</Text>
                <Text style={styles.HeadTextCap}>Water quality results</Text>
                <Text style={styles.Date}>{currentDate}<br/><Text style={{fontSize:11, fontWeight:500}}>{currentMonth}</Text></Text>
            </LinearGradient>
        </View>


        <View style = {styles.StatusContainer} >
          <LinearGradient
                // Button Linear Gradient
                colors={['#EEF2FF', '#ffffff']}
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style={styles.headContent}
                >
            <View style = {styles.StatusBarContainerHead}> 

            </View>
          
          </LinearGradient>
        </View>
        </View>
      
    </View>
  );
};

  // add this to a button
  const AppButton = ({ onPress, title }) => (
    <TouchableOpacity onPress={onPress} style={styles.appButtonContainer}>
      <Text style={styles.appButtonText}>{title}</Text>
    </TouchableOpacity>
  );

  const MyActivityIndicator = () => {
	return (
      	<View style={{ flex: 1, justifyContent: "center"}}>
      		//size can be "small" or "large"
			<ActivityIndicator size="large" color="#00ff00" />
      	</View>
    );
}

// export default MyActivityIndicator;
let deviceWidth = Dimensions.get('window').width

  const styles = StyleSheet.create({

   
      BackArrow:{
        // justifyContent: 'center',
        // alignItems: 'center',
        width:10,
        height:17,
        marginLeft:30,
        marginTop:50,
      },

      SelectArrow:{
        // justifyContent: 'center',
        // alignItems: 'center',
        position:"absolute",
        width:8,
        height:15,
        top:"45%",
        right:30,
        opacity:0.7,
      },

      Date:{
        position:"absolute",
        // fontFamily:"ubuntu",
        fontSize:19,
        fontWeight:"bold",
        color:"white",
        top:95,
        right:30,
        backgroundColor:"#030096",
        borderStyle:'solid',
        borderWidth:1,
        borderColor:"#030096",
        borderRadius:5,
        padding:7,
        paddingTop:10,
        paddingBottom:10,
        lineHeight: 15,
        textAlign: "center",
      },
      selectDate:{
        position:"absolute",
        // fontFamily:"ubuntu",
        fontSize:19,
        fontWeight:"bold",
        color:"white",
        top:"30%",
        left:30,
        backgroundColor:"#030096",
        borderStyle:'solid',
        borderWidth:1,
        borderColor:"#030096",
        borderRadius:5,
        padding:5,
        lineHeight: 15,
        textAlign: "center",
      },

      HeadText:{
        position:"absolute",
        // fontFamily:"ubuntu",
        fontSize:30,
        fontWeight:700,
        color:"#020058",
        top:"40%",
        left:30,
        Tex
      },
  
      HeadTextCap:{
        position:"absolute",
        fontSize:13,
        // fontWeight:"bold",
        color:"rgba(0, 20, 124, 0.86)",
        top:"15%",
        left:30,
      },


      container: {
        flex: 1,
        alignItems: "center",
        justifyContent: 'flex-end',
        backgroundColor: "#EFFBFF",
    },

    StatusContainer:{
      postion:"absolute",
      width:deviceWidth,
      top:"25%",
      height:"100%",
      // backgroundColor:"black",
    },

    StatusBarContainerHead:{
      postion:"absolute",
      width:deviceWidth-40,
      marginLeft:20,
      height:"30%",
      bottom:0,
      top:0,
      alignItems: "center",
      justifyContent: 'center',
      backgroundColor: "white",
      marginTop: 0,
      marginBottom: 0,
      // flexDirection: 'row',
      borderStyle:'solid',
      borderWidth:0,
      borderRadius:25,
      shadowColor: "rgba(0,0,0,0.6)",
      shadowOffset: {
        width: 0,
        height: 2,
      },
      shadowOpacity: 0.2,
      shadowRadius: 15,

      elevation: 12,
      
    },

    headContainer: {
        flex: 1,
        position:"absolute",
        width:deviceWidth,
        height:"30%",
    },


    headContent: {
        flex: 1,
        // width:Dimensions.get("window").width,
        // height: "10%",
    },


});
