import React, { useEffect, useState } from 'react';
import { FlatList,Button, Text, View, StyleSheet,ActivityIndicator,TextInput,TouchableOpacity, ScrollView ,Picker } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import TestingSensorPage from './TestingSensorPage';
import Spinner from 'react-native-loading-spinner-overlay';
import * as Animatable from 'react-native-animatable';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import Feather from 'react-native-vector-icons/Feather';
import { RadioButton } from 'react-native-paper';
// import RadioButton from './components/RadioButton';
// import RadioButtonRN from 'radio-buttons-react-native';
// import RadioButtonRN from 'radio-buttons-react-native';
// import RadioForm, {RadioButton, RadioButtonInput, RadioButtonLabel} from 'react-native-simple-radio-button';
import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    
    StackedBarChart
  } from "react-native-chart-kit";

  import { Dimensions,TouchableHighlight, Image } from "react-native";

  export default function SignUpPage({navigation}) {

 
//   const dataPie = {
//     labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
//     data: [,data.ph, data.temp, data.turbidity, data.conductivity]
//   };
const [checked, setChecked] = React.useState('first');
const [data,setData] =React.useState({
  UserName:'',
  FirstName:'',
  LastName:'',
  email:'',
  password:'',
  confirm_password:'',
  contact_no:'',
  NIC:'',
  
  check_textInputChangeU:false,
  check_textInputChangeF:false,
  check_textInputChangeL:false,
  check_textInputChange2:false,
  check_textInputChange3:false,
  check_textInputChange4:false,
  check_textInputChange5:false,
  secureTextEntry:true,
  confirm_secureTextEntry:true,
  isValidUserU:true,
  isValidUserF:true,
  isValidUserL:true,
  isValidPassword:true,
  isValidContactno:true,
  isValidNIC:true,
  isValidEmail:true,
  isValidConfirmPassword:true,
  isValidLocation:true

});

const textInputChangeU = (val)=>{
  if(val.trim().length >=4){
    setData({
      ...data,
      FirstName:val,
      check_textInputChangeU:true,
      isValidUserU:true
    });
  }
  else{
      setData({
        ...data,
        Username:'',
        check_textInputChangeU:false,
        isValidUserU:false

      });
  }
}
const textInputChangeF = (val)=>{
  if(val.trim().length >=4){
    setData({
      ...data,
      FirstName:val,
      check_textInputChangeF:true,
      isValidUserF:true
    });
  }
  else{
      setData({
        ...data,
        Username:'',
        check_textInputChangeF:false,
        isValidUserF:false

      });
  }
}
const textInputChangeL = (val)=>{
  if(val.trim().length >=4){
    setData({
      ...data,
      LastName:val,
      check_textInputChangeL:true,
      isValidUserL:true
    });
  }
  else{
      setData({
        ...data,
        Username:'',
        check_textInputChangeL:false,
        isValidUserL:false

      });
  }
}
const textInputChange2 = (val)=>{
  if(!isNaN(val) && val.trim().length==10){
    setData({
      ...data,
      contact_no:val,
      check_textInputChange2:true,
      isValidContactno:true
    });
  }
  else{
      setData({
        ...data,
        contact_no:'',
        check_textInputChange2:false,
        isValidContactno:false
      });
  }
}
const textInputChange3 = (val)=>{
  if(!isNaN(val) && val.trim().length==12){
    setData({
      ...data,
      NIC:val,
      check_textInputChange3:true,
      isValidNIC:true
    });
  }
  else if(val.slice(-1)=="v" && val.trim().length==12){
      setData({
          ...data,
          NIC:val,
          check_textInputChange3:true,
          isValidNIC:true
        });
  }
  else if(val.slice(-1)=="V" && val.trim().length==12){
      setData({
          ...data,
          NIC:val,
          check_textInputChange3:true,
          isValidNIC:true
        });
  }
  else{
      setData({
        ...data,
        NIC:'',
        check_textInputChange3:false,
        isValidNIC:false
      });
  }
}
const textInputChange4 = (val)=>{
  let reg = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
  if(val.trim().length>=10 && reg.test(val)===true){
    setData({
      ...data,
      email:val,
      check_textInputChange4:true,
      isValidEmail:true
    });
  }
  else{
      setData({
        ...data,
        email:'',
        check_textInputChange4:false,
        isValidEmail:false
      });
  }
}

const handelPasswordChange = (val) => {
  if(val.trim().length>=6){
      setData({
        ...data,
        password:val,
        check_textInputChange5:true,
        isValidPassword:true
      });
    }
    else{
      setData({
        ...data,
        password:'',
        check_textInputChange5:false,
        isValidPassword:false
      });
  }
}
const handelConfirmPasswordChange = (val) => {
    if(data.password==val){
      setData({
          ...data,
          confirm_password:val,
          check_textInputChange5:true,
          isValidConfirmPassword:true
        });
    }
    else{
      setData({
          ...data,
          confirm_password:'',
          check_textInputChange5:true,
          isValidConfirmPassword:false
        });
    }
 
}

const updateSecuretextEntry = () => {
  setData({
    ...data,
    secureTextEntry:!data.secureTextEntry
  });
}
const updateConfirmSecuretextEntry = () => {
  setData({
    ...data,
    confirm_secureTextEntry:!data.confirm_secureTextEntry
  });
}
const pressHandler = async () =>{
  if(data.email.length==0 || data.contact_no.length==0 || data.NIC.length==0 || data.Username.length==0 || data.password.length==0 || data.confirm_password.length==0){
      alert("Fill the following requirments");
  }
  else{
    try{
      fetch('http://127.0.0.1:5000/signup', {
        method: 'POST',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          user_name: data.UserName,
          user_f_name: data.FirstName,
          user_l_name: data.LastName,
          password: data.password,
