import React, { useEffect, useState } from 'react';
import { FlatList,Button, Text, View, StyleSheet,ActivityIndicator,Image } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import Spinner from 'react-native-loading-spinner-overlay';
// import TestsResults from '../assets/TestsResults.json';
import * as data from '../assets/TestsResults.json';
import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    StackedBarChart
  } from "react-native-chart-kit";

  import { Dimensions,TouchableHighlight,TouchableOpacity  } from "react-native";
import AdminSignin from './AdminSignin';

  export default function UserEditPage({navigation}) {

    
  // const [isLoading, setLoading] = useState(true);
  // const [data, setData] = useState([]);
  // console.log(data);

  
  // useEffect(() => {
  //   fetch(TestsResults)
  //     .then((response) => response.json())
  //     .then((json) => setData(json))
  //     .catch((error) => console.error(error))
  //     .finally(() => setLoading(false));
  // }, []);

  const dataPie = {
    labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
    data: [,(data.value_params.ph*7.14)/100, data.value_params.temp/100, (data.value_params.turbidity*2)/100, (data.value_params.conduct*2)/100]
  };

//   const dataPie = {
//     labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
//     data: [,data.ph, data.temp, data.turbidity, data.conductivity]
//   };

const [currentDate, setCurrentDate] = useState('');
const [currentMonth, setCurrentMonth] = useState('');
const [prevMonth, setPrevMonth] = useState('');

  useEffect(() => {
    var monthNames = [ 'JAN', 'FEB', 'MAR', 'APR', 'MAY','JUN',
    'JUL', 'AUG', 'SEP', 'OCT', 'NOV', 'DEC'];
    var date = new Date().getDate(); //Current Date
    var month = monthNames[new Date().getMonth()]; //Current Month
    var prevMonth = monthNames[data.month-1]; //Current Month
    // var year = new Date().getFullYear(); //Current Year
    // var hours = new Date().getHours(); //Current Hours
    // var min = new Date().getMinutes(); //Current Minutes
    // var sec = new Date().getSeconds(); //Current Seconds
    setCurrentDate(
      date 
    );
    setCurrentMonth(
      month 
    );
    setPrevMonth(
      prevMonth
    );
  }, []);

  var prevDate = data.date;
  var prevYear = data.year;

  const getUser = (UserId) => {
    while(true){
      if(UserId = signedinId){
        return "TestingPage"
      }
      var i = i + 1;
    }
    return "AdminSignin"
  }

  const user = () => {
    return "AdminSignin"
    // return "TestingPage"
  }



  return (

    <View style={{ flex: 1, padding: 0, backgroundColor:"white"}}>
      {/* {isLoading ? <Text>Loading...</Text> :           */}
          <View style={{ flex: 1, flexDirection: 'column', justifyContent:  'space-between'}}>


        
          <View style = {styles.headContainer}>
            <LinearGradient
                // Button Linear Gradient
                colors={['#020058', '#030096']} 
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style={styles.headContent}
                >
                <TouchableOpacity onPress={() => navigation.navigate('Dashboard')}>
                  <Image source={require('../assets/back-arrow.png')} style = {styles.BackArrow} />
                </TouchableOpacity>

                <Text style={styles.HeadText}>JalaRead</Text>
                {/* <Text style={styles.HeadTextCap}>Water quality results</Text> */}
            </LinearGradient>
        </View>

        <LinearGradient
                // Button Linear Gradient
                colors={['#EEF2FF', '#ffffff']}
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style = {styles.InnerHeadContent}>
                
            <Image source={require('../assets/admin-icon-pic.svg')} style = {styles.UserIcon} />
            <Text style = {styles.InnerHeadText}>Edit Page</Text>
            <Text style = {styles.InnerHeadTextCap}>Admin</Text>
            {/* <TouchableOpacity style={styles.BottomButtom} onPress={() => navigation.navigate('LocationInput')}>
              <Text style={styles.BottomButtomText}>Edit Profile</Text>
            </TouchableOpacity> */}

        </LinearGradient>

        <View style = {styles.StatusContainer} >
          <LinearGradient
                // Button Linear Gradient
                colors={['#EEF2FF', '#ffffff']}
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style={styles.headContent}
                >

              <View style = {styles.BottomContent}>               
                <Text style={styles.BttmCntntTxtCap}>Full Name</Text>
                <Text style={styles.BttmCntntTxt}>Jane Cooper</Text>
                <TouchableOpacity style={styles.edit}>Edit</TouchableOpacity>
                <Text style={styles.BttmCntntTxtCap}>Fulu Name</Text>
                <Text style={styles.BttmCntntTxt}>Jane Cooper</Text>
                <TouchableOpacity style={styles.edit}>Edit</TouchableOpacity>
              </View>

          </LinearGradient>
        </View>
        </View>
      
    </View>
  );
};

const linedata = {
  labels: ["JAN", "FEB", "MAR", "APR", "MAY"],
  datasets: [
    {
      data: [20, 45, 28, 80, 99],
      color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`, // optional
      strokeWidth: 4,// optional
      
    }
  ],
  legend: ["Quality Tests"] // optional
};

const chartConfig = {
  backgroundGradientFrom: "#030092",
  backgroundGradientFromOpacity: 1,
  backgroundGradientTo: "#0400C9",
  backgroundGradientToOpacity: 1,
  color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
  strokeWidth: 2, // optional, default 3
  barPercentage:0.5,
  useShadowColorFromDataset: false, // optional
  
};




  // add this to a button
  const AppButton = ({ onPress, title }) => (
    <TouchableOpacity onPress={onPress} style={styles.appButtonContainer}>
      <Text style={styles.appButtonText}>{title}</Text>
    </TouchableOpacity>
