import React, { useEffect, useState } from 'react';
import { FlatList, Text, View, StyleSheet } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    StackedBarChart
  } from "react-native-chart-kit";

  import { Dimensions  } from "react-native";

export default function App() {
  const [isLoading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  console.log(data);

  useEffect(() => {
    fetch('https://raw.githubusercontent.com/adhithiravi/React-Hooks-Examples/master/testAPI.json')
      .then((response) => response.json())
      .then((json) => setData(json))
      .catch((error) => console.error(error))
      .finally(() => setLoading(false));
  }, []);

  return (

    <View style={{ flex: 1, padding: 0 }}>
      {isLoading ? <Text>Loading...</Text> : 
      (
          
          <View style={{ flex: 1, flexDirection: 'column', justifyContent:  'space-between'}}>
          {/* <Text style={{ fontSize: 18, color: 'green', textAlign: 'center'}}>{data.title}</Text> */}
          {/* <Text style={{ fontSize: 14, color: 'green', textAlign: 'center', paddingBottom: 10}}>Articles:</Text> */}
          {/* <FlatList
            data={data.articles}
            keyExtractor={({ id }, index) => id}
            renderItem={({ item }) => (
              <Text>{item.id + '. ' + item.title}</Text>
            )}
          /> */}
        <View style = {styles.headContainer}>
            <LinearGradient
                // Button Linear Gradient
                colors={['#001A4B', '#002B7B']}
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style={styles.headContent}
                >
                <Text style={styles.HeadText}>Quality test</Text>
                <Text style={styles.HeadTextCap}>Water quality results</Text>
            </LinearGradient>
        </View>

          <View style = {styles.pieChart}>
     
                <ProgressChart
                data={dataPie}
                width={Dimensions.get("window").width}
                height={220}
                strokeWidth={12}
                radius={27}
                chartConfig={{
                    backgroundColor: "transparent",
                    backgroundGradientFrom: "transparent",
                    backgroundGradientTo: "transparent",
                    decimalPlaces: 2, // optional, defaults to 2dp
                    color: (opacity = 1) => `rgba(3, 0, 150, ${opacity})`,
                    labelColor: (opacity = 1) => `rgba(2, 0, 88, ${opacity})`,
                    style: {
                    borderRadius: 16,
                    },
                    propsForDots: {
                    r: "6",
                    strokeWidth: "2",
                    stroke: "#fff"
                    }
                }}
                hideLegend={false}
                />

                <Text>Note : All tests has to be 100%</Text>

            </View>  
        </View>
      )}
    </View>
  );
};

const dataPie = {
  
    labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
    data: [,0.4, 0.7, 0.8, 0.2]
  };

  const styles = StyleSheet.create({

    HeadText:{
        position:"absolute",
        // fontFamily:"ubuntu",
        fontSize:22,
        fontWeight:"bold",
        color:"white",
        top:63,
        left:30,
      },
  
      HeadTextCap:{
        position:"absolute",
        fontSize:12,
        // fontWeight:"bold",
        color:"#D1E1FF",
        top:93,
        left:30,
      },

      container: {
        flex: 1,
        alignItems: "center",
        justifyContent: 'flex-end',
        backgroundColor: "#EFFBFF",
    },
    headContainer: {
        flex: 1,
        position:"absolute",
        width:Dimensions.get("window").width,
        height:"20%",
    },
    headContent: {
        flex: 1,
        // width:Dimensions.get("window").width,
        // height: "10%",
    },

    pieChart:{
        // flex: 1,
        postion:"absolute",
        bottom:200,
        left:-27,
        alignItems: "center",
        justifyContent: 'flex-end',
        backgroundColor: "transparent",
        marginTop: 400
        // paddingTop: 200,
      },

});
