import React, { useEffect, useState } from 'react';
import { FlatList,Button, Text, View, StyleSheet,ActivityIndicator } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import Spinner from 'react-native-loading-spinner-overlay';
import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    StackedBarChart
  } from "react-native-chart-kit";

  import { Dimensions  } from "react-native";

export default function App() {

  const [isLoading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  console.log(data);

  useEffect(() => {
    fetch('https://raw.githubusercontent.com/adhithiravi/React-Hooks-Examples/master/testAPI.json')
      .then((response) => response.json())
      .then((json) => setData(json))
      .catch((error) => console.error(error))
      .finally(() => setLoading(false));
  }, []);

  const dataPie = {
    labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
    data: [,0.4, 0.7, 0.8, 0.3]
  };

//   const dataPie = {
//     labels: [,"pH", "Temp", "Turbidity","Conductivity"], // optional
//     data: [,data.ph, data.temp, data.turbidity, data.conductivity]
//   };


  return (

    <View style={{ flex: 1, padding: 0 }}>
      {isLoading ? <Text>Loading...</Text> : 
      (
          
          <View style={{ flex: 1, flexDirection: 'column', justifyContent:  'space-between'}}>

        <View style = {styles.headContainer}>
            <LinearGradient
                // Button Linear Gradient
                colors={['#001A4B', '#002B7B']}
                start={{ x: 1, y: 1 }}
                end={{ x: 0, y: 1 }}
                style={styles.headContent}
                >
                <Text style={styles.HeadText}>Dashboard</Text>
                <Text style={styles.HeadTextCap}>Welcome to JalaRead</Text>
            </LinearGradient>
        </View>

          <View style = {styles.pieChart}>
                
                <Text> </Text>

            </View>  
        </View>
      )}
    </View>
  );
};

  const AppButton = ({ onPress, title }) => (
    <TouchableOpacity onPress={onPress} style={styles.appButtonContainer}>
      <Text style={styles.appButtonText}>{title}</Text>
    </TouchableOpacity>
  );

  const MyActivityIndicator = () => {
	return (
      	<View style={{ flex: 1, justifyContent: "center"}}>
      		//size can be "small" or "large"
			<ActivityIndicator size="large" color="#00ff00" />
      	</View>
    );
}

// export default MyActivityIndicator;

  const styles = StyleSheet.create({

    appButtonContainer: {
        elevation: 8,
        backgroundColor: "#009688",
        borderRadius: 10,
        paddingVertical: 10,
        paddingHorizontal: 12
      },
      appButtonText: {
        fontSize: 18,
        color: "#fff",
        fontWeight: "bold",
        alignSelf: "center",
        textTransform: "uppercase"
      },

    HeadText:{
        position:"absolute",
        // fontFamily:"ubuntu",
        fontSize:22,
        fontWeight:"bold",
        color:"white",
        top:63,
        left:30,
      },
  
      HeadTextCap:{
        position:"absolute",
        fontSize:12,
        // fontWeight:"bold",
        color:"#D1E1FF",
        top:93,
        left:30,
      },

      container: {
        flex: 1,
        alignItems: "center",
        justifyContent: 'flex-end',
        backgroundColor: "#EFFBFF",
    },
    headContainer: {
        flex: 1,
        position:"absolute",
        width:Dimensions.get("window").width,
        height:"20%",
    },
    headContent: {
        flex: 1,
        // width:Dimensions.get("window").width,
        // height: "10%",
    },

    pieChart:{
        // flex: 1,
        postion:"absolute",
        bottom:100,
        left:-27,
        alignItems: "center",
        justifyContent: 'flex-end',
        backgroundColor: "transparent",
        marginTop: 400
        // paddingTop: 200,
      },

});
